mapper.select:
select id
from orderItem
where uid = #{0}
  and pid = #{1}
  and success = 0;

select *
from orderItem
where uid = #{0}
  and pid = #{1}
  and success = 0;

mapper.update:
update orderItem
set num=#{num},
    success=#{success}
where id = #{id}

update orderItem
set success=1
where uid = #{uid}
